void mainImage(out vec4 O, in vec2 U)
{
    O = vec4(S0(UV(U)).rgb, 1);
}

void mainImage(out vec4 O, in vec2 U)
{    
    O = comp(U,iMouse,iFrame==0?vec4(1):S0(UV(U)),vec3(abs(cos(iTime)),.3,.2),max(abs(sin(iTime)),13.)*4.,abs(cos(iTime)));
}

#define UV(U) U.xy/iResolution.xy
#define S0(UV) texture(iChannel0, UV)
#define OVER(S,D) vec4(S.xyz*S.a + D.xyz*(1.0 - S.a), 1.0)
vec4 comp(vec2 U, vec4 M,vec4 d,vec3 col,float size,float aMod) {
    float dst = distance(vec2(U.xy),vec2(M.xy));    
    if (dst<size && M.z>.0) {
        return OVER(vec4(col,(1.-dst/size)*0.65*aMod),d);  
    }return d;
}
