#define pi 3.14159

// generated with http://dev.thi.ng/gradients/
vec3 palette( float t ) {
    vec3 a = vec3(0.000,0.500,0.500);
    vec3 b = vec3(0.000,0.500,0.500);
    vec3 c = vec3(0.000,0.500,0.333);
    vec3 d = vec3(0.000,0.500,0.667);

    return (a + b*cos( 6.28318*(c*t+d) ));
}

// rotate coordinate around (0, 0)
vec2 rotated(vec2 coords, float angle){
    vec2 result;
    angle = angle * pi / 180.;
    result.x = coords.x * cos(angle) + coords.y * sin(angle);
    result.y = -coords.x * sin(angle) + coords.y * cos(angle);
    return result;
}

// get height of wave depending on the speed of the wave and the distance to the center
float gh(float dist, float speed){
    dist = dist * -7.;
    return sin(dist + iTime * (8.+ 4.* speed)) -.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    // zoom
    uv *= 10.;

    // distance to five Points around the center
    const float radius = 5.;     
    float a = length(uv - rotated(vec2(0, radius), 72. * 0.));
    float b = length(uv - rotated(vec2(0, radius), 72. * 1.));
    float c = length(uv - rotated(vec2(0, radius), 72. * 2.));
    float d = length(uv - rotated(vec2(0, radius), 72. * 3.));
    float e = length(uv - rotated(vec2(0, radius), 72. * 4.));

    // get summ of heights of the waves comming from the Points
    float height = (gh(a, 0.) + gh(b, .4) + gh(c, .8) + gh(d, .2) + gh(e, .6)) / 5.;
    // scale from 0 to 1 for the pallete
    height += 1.;
    height /= 2.;

    fragColor = vec4(palette(height), 0);
}
