#define   S     smoothstep
#define   A     min( R.x, R.y )
#define   h(u)  fract(dot(u.yxx * .1234, ceil(u.yxx) + 12.34) * 123.456)
#define   l(a)  S( .1, .9, sin( a*.5 ) / .5 )
#define rot(a)  mat2( cos( a + vec4(0, 11, 33, 0) ) )

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         u = ( U+U - R ) / A,
         a = round( u / .2 ),
         u2 = abs( u -= .2 * a );   
    u *= rot( S(0.,1., S(.5,0., abs( fract( l(iTime)*.5 ) - .5 ) ))
              * 1.57* round( h( abs(a*9.) ) ) );

    if( round( h( abs(a-20.) ) ) > 0. )
        u.x *= -1.;

    O = vec4(  S( 6./A, 0., 
                  abs(   length( u - .1*sign(u.x+u.y) )
                       + max( u2.x, u2.y ) * 4.
                       -.5
            )   )    );
}
