#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime

vec3 r33(vec3 p3){
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

void mainImage(out vec4 o, vec2 u) {    
    vec2 r = iResolution.xy; 
         u = (u - r.xy / 2.) / r.y;

    o -= o;
    vec3 D = normalize(vec3(u, 2)), f, q, rnd, 
         p = vec3(3, 1, 2);
    float i, j, d = 1., s;

    D.zy *= rot(.13 * t);
    D.zx *= rot(.12 * t);
    p.zy *= rot(.13 * t);
    p.zx *= rot(.12 * t);

    while(i++ < 320. && d > .01){

        rnd = r33(floor(p));
        q = fract(p + .3 * cos(rnd * t + vec3(0, 11, 0))) - .5;

        s = 3.;
        d = length(q * s * .2) - .08;
        d *= 4./s;

        j = 1.;
        while(j < 8.){
            j += j,
            f = q * j * 4., 
            f += rnd * t + rnd;
            d += abs(dot(sin(f), q/q)) / j * .06;
        } 

        d = max(d, -abs(q.z) - .1) - .01;
        d *= .2;

        p += d * D;
    }

    j = .4;
    o.rgb += j /(length(q)  + j) + rnd * .2;
    o *= o * o * o;
}
