// https://www.shadertoy.com/view/dsKczW neve
// https://www.shadertoy.com/view/ddKcRD titanic

#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime

void mainImage(out vec4 o, vec2 u) {    
    vec2 q, r = iResolution.xy; 
            u = (u - r.xy / 2.) / r.y;

    o -= o;
    vec3 D = normalize(vec3(u, 3. + 2. * cos(t*.1)) * r.y), f, 
         p = vec3(
                 sin(t * .78) * 1. - 1.,
                 cos(t) * .2, 
                 t * 3.),
         k = p; 

    k += vec3(
            cos(1.*t), 
            sin(2.*t), 
            sin(3.*t) * 2. + 7. + 10.
         ) * .2;

    float i, j, water, d = 1.;

    D.zy *= rot(sin(t) * .08);
    D.zx *= rot(cos(t * .1) * .2);

    while(i++ < 120. && d > .02){
        water = abs(p.y + cos(t*1.4) * .2 + .1) - .02;
        q = mod(p.xz, vec2(2,4)) - vec2(1, 2),
        d = min(water, -abs(p.y) + .4),

        j = 1.;
        while(j < 512.) 
            j += j,
            f = (p + vec3(p.xy, p.z * (d == water?3.:0.))) * j * 14., 
            f.yz *= rot(j),
            d += abs(dot(sin(f*.1), vec3(2))) / j * .04;

        d = min(d, min(length(p - k) - .1, length(q) - .1)),

        p += D * d;

    }
    o.y += 2. / i; 
    o += .05 * vec4(8, 9, 12, 0) / length(p - k); 
}
