vec3 palette( float t)
{
vec3 a = vec3(0.500, 0.508, 0.500);
vec3 b = vec3(-0.462, -0.092, -0.242);
vec3 c = vec3(2.008, 0.858, 1.048);
vec3 d = vec3(-1.372, 0.148, 1.2987);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    float uv0 = 8.0*sin(0.9*iTime)*length(uv);
     vec3 finalColor = vec3(0.0);
    uv = fract(3.0*uv*uv0)-0.5;
    vec3 col =palette(length(uv0)+ iTime*0.9);
    float d = length(uv);

    d = step(step(0.43,d),d);
    finalColor += col*d;
    fragColor = vec4(finalColor,1.0);
}
