#define hash22(u) textureLod(iChannel1, (u+.5)/256.0, 0.0).xy
#define hash23(u) textureLod(iChannel1, (u+.5)/256.0, 0.0).xyz
#define dot2(p) dot(p,p)

float voronoi(in vec2 uv, inout vec2 point)
{
    vec2 ip = floor(uv);

    float md = 100.0;
    vec2 mp = vec2(md);
    vec2 mk;

    for (float i = -1.0; i < 1.01; ++i)
    for (float j = -1.0; j < 1.01; ++j)
    {
        vec2 g = ip + vec2(i,j);
        vec3 h = .9*hash23(g);

            vec2 p = g + h.xy;
            vec2 k = uv - p;
            float d = dot(k,k);
            if (d < md)
            {
                md = d;
                mp = p;
                mk = k;
            }
    }

    md = 100.0;
    for (float i = -2.0; i < 2.01; ++i)
    for (float j = -2.0; j < 2.01; ++j)
    {
        vec2 g = ip + vec2(i,j);
        vec2 h = .9*hash22(g);
        vec2 p = g + h;
        vec2 k = uv - p;

        if (dot2(k - mk) > 0.00001)
            md = min(md, dot(0.5 * (mk + k), normalize(k - mk)));
    }

    point = mp;
    return md;
}

void mainImage( out vec4 O, in vec2 F )
{
    vec2 R = iResolution.xy;

    if (F == R-.5)
    {
        O.a = iChannelResolution[1].x;
        return;
    }

    if (0 == iFrame || texture(iChannel0, 1. - .5/R, .0).a == .0 || texelFetch(iChannel3, ivec2(F), 0).rg != R)
    {
        vec2 uv = (2.0*F - R.xy)/R.y;

        vec2 id;
        float d = voronoi(uv*50., id);
        id = vec2(id.x*.125+5., id.y)+50.;
        vec3 c = abs(uv.x)>1.2?vec3(1):texture(iChannel2,id*.01).rgb;
        c = mix(c, vec3(0), smoothstep(1.0,-1.0,(d-.05)/fwidth(d)));
        O = vec4(sqrt(c),1.0);
    }
    else
    {
        O = texture(iChannel0, F/R);
    }
}

