void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float aspect = iResolution.y/iResolution.x;
    float value;
	vec2 uv = fragCoord.xy / iResolution.x;
    float rot = radians(45.0); // radians(45.0*sin(iTime));
    mat2 m = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
   	uv  = m * uv;
    uv += vec2(0.5, 0.5*aspect);
    uv.y+=0.5*(1.0-aspect);
    uv += vec2(iTime*0.05, 0);

    float s = 0.2;
    float w = 0.005;

    vec3 bg = vec3(0.0);
    vec3 fg = vec3(0.62, 0.51, 0.11);

    vec3 col = bg;

    if (mod(uv.x, s) < w || mod(uv.x, s) > s-w) {
        col = fg;
        }
    if (mod(uv.y, s) < w || mod(uv.y, s) > s-w) {
        col = fg;
        }


    vec2 llc = vec2(uv.x - mod(uv.x, s), uv.y - mod(uv.y, s));

    float xd = uv.x-llc.x;
    float yd = uv.y-llc.y;

    float s2 = s/3.0;
    float s3 = s/3.0*2.0;

    if (xd < s2+w && xd > s2-w && yd < s2+w) {
        col=fg+vec3(0.2);
        }
    if (yd < s2+w && yd > s2-w && xd < s2+w) {
        col=fg+vec3(0.2);
        }
    if (xd < s3+w && xd > s3-w && yd < s3+w) {
        col=fg+vec3(0.1);
        }
    if (yd < s3+w && yd > s3-w && xd < s3+w) {
        col=fg+vec3(0.1);
        }
    if (mod(uv.x, s) < w || mod(uv.x, s) > s-w) {
        col = fg;
        }
    if (mod(uv.y, s) < w || mod(uv.y, s) > s-w) {
        col = fg;
        }




    // Output to screen
    fragColor = vec4(col,1.0);
}
