// version of https://www.shadertoy.com/view/csdcR8
#define r(a) mat2(cos(a + vec4(0, 33, 11, 0)))

void mainImage(out vec4 O, vec2 u) {
    vec3  p = iResolution; 
          u = (u + u - p.xy) / p.y;  

    float x, i, d, a, T = iTime * .5;

    O *= 0.;
    while(i++ < 140.){
        p = .7 * vec3(u * a, a - .05);

        p.yx *= r(T * 1.73);
        p.xz *= r(T * 2.); 

        d = .5 / dot(p, p);
        p *= d * .4;

        int j, k = 5; 
        while(j++ < k) 
            p.z = abs(p.z), 
            p.xz *= r(3.14/float(k));

        p = abs(cos(p)) * cos(.35 *  p.z) * 8. + .0001;

        a += d = .6 * p.y / d;

        O += vec2(
                 .005 / exp(20000. * d + .9),
                 .01 / exp(5000. * sqrt(abs(d)) + 1.2)
             ).yyxx * 2.;
    }
}
