void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-0.5;
    vec2 offset = vec2(sin(iTime*2.)*0.5, cos(iTime*2.)*0.5);
    uv.x -= offset.x;
    uv.y -= offset.y;

    // Time varying pixel color
    vec3 col =0.5+0.9*sin(-iTime+(uv.x*uv.x)+(uv.y*uv.y)+vec3(8,1,3))
    *cos(iTime+(uv.y*uv.x)+(uv.y*uv.y)+vec3(5,1,6))
    +0.5*cos(iTime+(uv.y*uv.x)+(uv.y*uv.y)+vec3(1,2,3))
    *cos(iTime+(uv.y*uv.x)
    +(uv.y*uv.y)+vec3(13,5,8))*cos(iTime+(uv.y*uv.x)
    +(uv.y*uv.y)+vec3(1,2,1))+0.5*cos(iTime+(uv.y*uv.x)+(uv.y*uv.y)+vec3(1,2,3));

    // Output to screen
    fragColor = vec4(col,1.0);
}
