#define resolution 10.
#define range 4.

float pix;

float plotCircle(vec2 uv, vec2 c, float r) {
    float cir = length(uv-c)-r;
    return smoothstep(4.*pix, 0., cir);
}

vec2 getPos(vec2 uvi){
    vec2 pos = vec2(random(uvi));
    return sin(pos * PI * 2. + iTime*0.5) * range + uvi;
    //return vec2(0.5) * range + uvi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y*resolution;
    float ixy = iResolution.x / iResolution.y;
    pix = 1. / iResolution.y * resolution;
    vec3 c_fin = vec3(0, 0.808, 0.788);

    vec2 uvi = floor(uv);
    vec2 uvf = fract(uv);

    #if 0
        float b1 = smoothstep(2.*pix,0.,abs(uvf.x));
        float b2 = smoothstep(2.*pix,0.,abs(uvf.x-1.));
        float b3 = smoothstep(2.*pix,0.,abs(uvf.y));
        float b4 = smoothstep(2.*pix,0.,abs(uvf.y-1.));
        float b = b1+b2+b3+b4;
        c_fin+=b;
    #endif


     float dist_min = 4.;
     for(float x = -range*2.; x <= range*2.; x += 1.) {
        for(float y = -range*2.; y <= range*2.; y += 1.) {
            if (x==0. && y ==0.) continue;


            vec2 neighbor = getPos(uvi + vec2(x,y));
            vec2 current = getPos(uvi);

            #if 0
            float cir = plotCircle(uv, neighbor, 4.*pix);
            c_fin += cir;
            float cir2 = plotCircle(uv, current, 6.*pix);
            c_fin += cir2 * vec3(1.,0.,0.);
            #endif

            dist_min = min(dist_min, length(neighbor - uv));
        }
    }
    vec3 color = palette(dist_min);
    c_fin = dist_min * color;
    //c_fin = mix(c_fin, color, dist_min);

    fragColor = vec4(c_fin,1.0);
}

#define PI 3.1415926

float random(float x) {
    float v = sin(x) * 4523.1254 + 1234.1313;
    return v * 0.5 + 0.5;
}
vec2 random( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// https://www.shadertoy.com/view/4dS3Wd
float hash(float p) {
  p = fract(p * 0.011);
  p *= p + 7.5;
  p *= p + p;
  return fract(p);
}
float hash(vec2 p) {
  vec3 p3 = fract(vec3(p.xyx) * 0.13);
  p3 += dot(p3, p3.yzx + 3.333);
  return fract((p3.x + p3.y) * p3.z);
}

float noise(float x) {
    float i = floor(x);
    float f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return mix(hash(i), hash(i + 1.0), u);
}

float noise(vec2 x) {
  vec2 i = floor(x);
  vec2 f = fract(x);

	// Four corners in 2D of a tile
  float a = hash(i);
  float b = hash(i + vec2(1.0, 0.0));
  float c = hash(i + vec2(0.0, 1.0));
  float d = hash(i + vec2(1.0, 1.0));

    // Simple 2D lerp using smoothstep envelope between the values.
	// return vec3(mix(mix(a, b, smoothstep(0.0, 1.0, f.x)),
	//			mix(c, d, smoothstep(0.0, 1.0, f.x)),
	//			smoothstep(0.0, 1.0, f.y)));

	// Same code, with the clamps in smoothstep and common subexpressions
	// optimized away.
  vec2 u = f * f * (3.0 - 2.0 * f);
  return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}
float noise(vec3 x) {
  const vec3 step = vec3(110, 241, 171);

  vec3 i = floor(x);
  vec3 f = fract(x);

    // For performance, compute the base input to a 1D hash from the integer part of the argument and the 
    // incremental change to the 1D based on the 3D -> 1D wrapping
  float n = dot(i, step);

  vec3 u = f * f * (3.0 - 2.0 * f);
  return mix(mix(mix(hash(n + dot(step, vec3(0, 0, 0))), hash(n + dot(step, vec3(1, 0, 0))), u.x), mix(hash(n + dot(step, vec3(0, 1, 0))), hash(n + dot(step, vec3(1, 1, 0))), u.x), u.y), mix(mix(hash(n + dot(step, vec3(0, 0, 1))), hash(n + dot(step, vec3(1, 0, 1))), u.x), mix(hash(n + dot(step, vec3(0, 1, 1))), hash(n + dot(step, vec3(1, 1, 1))), u.x), u.y), u.z);
}

vec3 palette(float t) {
  vec3 a = vec3(0.731, 1.098, 0.192);
  vec3 b = vec3(0.358, 1.090, 0.657);
  vec3 c = vec3(1.077, 0.360, 0.328);
  vec3 d = vec3(0.965, 2.265, 0.837);
  return a + b * cos(6.28318 * (c * t + d));
}
