#define R3D(a,A) mix(A*dot(p,A), p, cos(a)) + sin(a)*cross(p,A)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=4.,e,t=iTime; vec3 p,q=vec3(4,5,2),r=iResolution;
for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1);
    p.xz*=R2D(t*.3);
    p*=mod(p,.1);

    for(j=0.;j++<4.;)
        p=abs(p)*e*R3D(e,q)-.04,
        d=min(max(p.z,p.y),p.x)/d,
        d-=dot(p,q)/2e4;

    e+=d*log(d);
}o+=vec4(p,1)*3./i;
}
