vec2 marble(
    inout vec2 coord,
    inout vec4 blend,
    vec2 pos,
    vec3 col,
    float rad
) {
    float dist = rad / (rad + length(pos - coord));
    vec2 dir = normalize(pos - coord);
    coord += dist * dir * rad;
    blend = mix(
        blend,
        vec4(col, 1.0),
        step(length(coord - pos), rad)
    );
    return coord;
}
float squamble(float amt, float t) {
    return sin(t / 3.0 + cos(t * 3.0) + cos(t * 1.0) * 3.0) * amt;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 coord = fragCoord;
    vec4 blend = vec4(0.0);
    vec2 pos_1 = iResolution.xy / 2.0 + vec2(
        squamble(480.0, iTime + 00.0),
        squamble(480.0, iTime + 50.0)
    );
    vec2 pos_2 = iResolution.xy / 2.0 + vec2(
        squamble(480.0, iTime + 100.0),
        squamble(480.0, iTime + 150.0)
    );
    vec2 pos_3 = iResolution.xy / 2.0 + vec2(
        squamble(480.0, iTime + 200.0),
        squamble(480.0, iTime + 250.0)
    );
    vec2 pos_4 = iResolution.xy / 2.0 + vec2(
        squamble(480.0, iTime + 300.0),
        squamble(480.0, iTime + 350.0)
    );
    vec2 pos_5 = iResolution.xy / 2.0 + vec2(
        squamble(480.0, iTime + 400.0),
        squamble(480.0, iTime + 450.0)
    );
    marble(coord, blend, pos_1, vec3(0.0, 0.0, 0.0), 45.0);
    marble(coord, blend, pos_2, vec3(1.0, 1.0, 0.5), 45.0);
