    marble(coord, blend, pos_5, vec3(1.0, 1.0, 1.0), 45.0);
    vec4 color = texture(
        iChannel0,
        coord / iResolution.xy
    );
    color.rgb = mix(color.rgb, blend.rgb, blend.a);
    fragColor = color;
}
