vec3 palette( float t ){
     vec3 a = vec3(.5,.5,.5);
     vec3 b = vec3(.5,.5,.5);
     vec3 c = vec3(1.,1.,1.);
     vec3 d = vec3(0.263,0.416,0.557);
     return a+b*cos( 6.28318*(c*t+d) );
}

float sdHexagram( in vec2 p, in float r )
{
    const vec4 k = vec4(-0.5,0.86602540378,0.57735026919,1.73205080757);

    p = abs(p);
    p -= 2.*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y)*cos(p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;

    uv.x*=iResolution.x/iResolution.y;

    vec3 fcol = vec3(0.);

    vec2 uv0=uv;

    uv.x+=sin(uv.y*length(uv0)+iTime)/length(uv0);

    uv.y+=cos(uv.x*length(uv0)+iTime)/length(uv0);

    for (float i =0.0; i<sdHexagram(uv,.25)+length(uv0)*4.; i++){

        uv*=1.5;

        uv=fract(uv);

        uv-=0.5;

        vec2 p = (2.*fragCoord-iResolution.xy)/iResolution.y;

        float d = sdHexagram(uv0,2.)*exp(-length(uv));

        float f=12.;

        d=sin(d*f+iTime)/f;

        d=abs(d);

        d=pow(0.01/d,1.2);

        vec3 col = tan(palette(length(uv0) + i*0.4 + iTime*0.4));

        fcol+=col*d;
    }
    fragColor = vec4(fcol,1.0);
}
