void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    vec3 background = vec3(0.5 + 0.5 * sin(uv.xyx * 10.0 + iTime));

    float noise = fract(sin(dot(uv * 10.0, vec2(10, 15))) * 1000.0);
    noise += 0.5 * fract(sin(dot(uv * 10.0, vec2(10, 15)) + iTime) * 1000.0);
    vec3 noiseOverlay = vec3(noise);

    float sphere = length(uv) - 0.5;
    vec3 shapeColor = vec3(0.5 + 0.5 * sin(100.0 * sphere + iTime));
    vec3 shape = mix(background, shapeColor, smoothstep(0.01, 0.02, -sphere));

    vec3 finalColor = mix(shape, noiseOverlay, 0.1);

    fragColor = vec4(finalColor, 1.0);
}
