#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec2 N(float angle){
    return vec2(sin(angle), cos(angle));
}

float Hash21(vec2 p){
    return fract(dot(p.x*64.33, p.y*74.242)*71.11+93.23);
}

float sdSphere(vec3 p, float r){
    return length(p)-r;
}



float GetDist(vec3 p) {
    p.xz *= Rot(iTime);
    p.xy *= Rot(iTime*1.5);
    float box1 = sdBox(p, vec3(1.2));
    p.xz *= Rot(-2.*iTime);
    float box2 = sdBox(p, vec3(1.));

    float s1 = sdSphere(p, 1.5);
    float s2 = sdSphere(p, 1.2);
    float s3 = sdSphere(p, .5);

    float g = smoothstep(.3, .2, length(p));

    s1 = max(s1, -g);
    float d = max(-box1, s1);
    d = min(d, max(s2, -box2));
    d = min(d, s3);

    d += g;
    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

float N21(vec2 p){
    return fract(dot(p.x*46.23, p.y*47.42)*47.411+93.23);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        col = n*.5+.5;
        col *= texture(iChannel0, r).rgb*.5+.4;
    }

    col *= sin(-2.*T+length(-uv)+vec3(2.134, 2.768, 2.923))*.5+.6;

    col = pow(col, vec3(.45));	

    fragColor = vec4(col,1.0);
}
