void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy - 0.5;

    vec4 k = vec4(0.0, 0.0, 0.0, 1.0);

    float passes = 100.0;
    float t = iTime * 0.08;

    for (float i = 0.0; i < passes; ++i) {
      float offset = i / passes;
      vec2 s = uv * offset + vec2(sin(t), cos(t));

      k += texture(iChannel0, s) / passes * 1.5;
    }


    fragColor = k;
}
