float noise(in vec2 p) {
    return sin(p.x * 12.9898 + p.y * 78.233) * 43758.5453;
}

vec2 hash2(vec2 p) {
    p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)));
    return fract(sin(p) * 43758.5453);
}


float sdCircle(vec2 p, float r) {
    return length(p - vec2(0.0, 0.0)) - r;
}

vec3 convertRGBtoVec3(vec3 color) {
    return vec3(color.r / 255., color.g / 255., color.b / 255.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    float time = iTime * 2.5;

    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.;
    float d = sdCircle(uv - sin(uv.x * time * 0.75), 0.5);
    d /= 0.05 + 0.05 * abs(10.*sin(time * 0.5)) + 0.05 * abs(sin(time * 0.2 + uv.y * 10.0));

    vec3 color = vec3(smoothstep(.75, 0.25, d));
    color = mix(color, vec3(1.0), smoothstep(0.5, 0., d));

    float glow = 0.1 / d;
    color += vec3(0.1, 0.1, 0.1) * glow;
    color = mix(color, vec3(1.0), smoothstep(0.05, 0., d));

    color /= sdCircle(uv / color.xy * sin(time + uv.x * 2.), 0.25);

    fragColor = vec4(color * convertRGBtoVec3(vec3(29., 185., 84.)), 1.0);
}
