#define r(a) mat2(cos(a+vec4(0,11,33,0)))

void mainImage( out vec4 O, vec2 F ) {
    vec2 R = iResolution.xy,
         u = (F+F-R) / R.y,
         t = u*r(-iTime*.05);    
    vec3 c;

    float T = iTime*.5, g = length(t) - .1, 
    r,b,a,d; 

    g +=(.1) * cos(atan(t.x*.6, t.y-abs(.2))-T);
    r = abs(g-.09);
    g = pow(.1/smoothstep(0.,.3,g), 1.5);
    r = pow(.009/r, .18);

    c = mix(vec3(0.,.808,.745), c, 1.-g);
    c = mix(vec3(1.,.412,.471), c, 1.5-r);

    for(a = 0.; a < 10.; a++) {
        t = u*r(iTime*.1);
        d = length(u) - .5;
        d += (.3*sin(a*.05+T)-.05) * cos(atan(u.x - b, t.y-.5)*10.+T*2.);

        d = pow(.01 / smoothstep(0.,.09,abs(d)+.005), 1.);

        c = mix(vec3(.471,.53,1.), c, 1.-d);
        b += .5;
    }

    O = vec4(c,1.);
}
