float dot2(vec2 v) {
  return dot(v,v);
}

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}
// r=radius, h=height
float sdCutDisk( in vec2 p, in float r, in float h )
{
    float w = sqrt(r*r-h*h); // constant for a given shape

    p.x = abs(p.x);

    // select circle or segment
    float s = max( (h-r)*p.x*p.x+w*w*(h+r-2.0*p.y), h*p.x-w*p.y );

    return (s<0.0) ? length(p)-r :        // circle
           (p.x<w) ? h - p.y     :        // segment line
                     length(p-vec2(w,h)); // segment corner
}

vec2 rotate(in vec2 p, float angle) {
  float s = sin(angle);
  float c = cos(angle);
  return vec2(c*p.x - s*p.y, 
              s*p.x + c*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (origin at center)
    vec2 p = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    // Background color:
    vec3 col = 0.5*(0.5 + 0.5*cos(iTime+p.xyx+vec3(0,2,4)));

    // Bounce:
    p.y -= 0.1*(abs(cos(iTime))-0.5) + 0.1*m.y*(abs(cos(5.*iTime)*cos(8.*iTime)));

    // Pumpkin colors:
    float flicker  = 0.9 + 0.1*abs(sin(23.*iTime)*cos(17.*iTime)*cos(3.*iTime)*cos(9.*iTime));
    vec3  INSIDE   = vec3(1.0, 1.0, 0.0) * flicker;
    vec3  REDHEART = vec3(240., 58., 23.)/255.;

    float heartiness = iMouse.y/iResolution.y; // interaction

    // BODY:
    {
        float bodyR      = 0.7;
        float ny         = p.y/bodyR; 
        float nx         = p.x/bodyR; 
        float width      = sqrt(1.0-ny*ny);
        float X          = nx/width;
        float sdfBody    = sdCircle(p, bodyR);
        float outline = 1.-smoothstep(-0.02, 0.01, sdfBody);
        float regPumpkin = 1.-smoothstep(0., 0.01, sdfBody);// 1 inside pumpkin
        float pumpkinLines = clamp(0.3+abs(3.5*cos(9.6*nx/width+0.4)), 0., 1.);//darkening
        vec3  colPumpkin = vec3(0.8, 0.5, 0.01) * pumpkinLines * outline;
        if(sdfBody < 0.) {// Color variations:
           float variations = 0.1 + 0.9*abs(sin(23.*X)*cos(17.*X)*cos(3.*X)*cos(9.*X));
           colPumpkin = mix(colPumpkin, vec3(255.,200.,61.)/255., variations);
        }
        col = mix(col, colPumpkin, regPumpkin);
    }

    // HEART EYES:
    {
        float eyeS = 2.;
        vec2  eyeTrans = vec2(.23,0.00);
        float eyeL = sdHeart((rotate(p+eyeTrans,-0.28)-vec2(0.,0.0))*eyeS)/eyeS;
        float eyeR = sdHeart((rotate(p-eyeTrans,+0.28)-vec2(0.,0.0))*eyeS)/eyeS;
        float sdfEyes = min(eyeL, eyeR);
        float regEyes = 1.-smoothstep(0., 0.01, sdfEyes);
        float outline = 1.-smoothstep(-0.02, 0.01, sdfEyes);

        vec3  colEyes = (INSIDE + 0.1*sin(-202.*sdfEyes + 20.*iTime)*vec3(1.0)) * outline;

        colEyes = mix(INSIDE, REDHEART,  0.7*sin(-202.*sdfEyes + 20.*iTime)+ abs(sdfEyes)*33.*heartiness)*outline;
        col = mix(col, colEyes, regEyes);
    }

    // MOUTH:
    {
        float s   = 1.0 - 0.25*heartiness;
        float sdf = sdCutDisk(vec2(p.x,-p.y-0.1), 0.4+0.05*heartiness, 0.05*s);
        float reg = 1.-smoothstep(0., 0.01, sdf);// 1 inside pumpkin
        float outline = 1.-smoothstep(-0.02, 0.01, sdf);
        vec3  mycol = INSIDE * outline;
        if(p.y>-0.25+0.02*heartiness) mycol *= 0.5; // teeth?!? :/
        col = mix(col, mycol, reg);

        // Tongue (why do pumpkins have tongues? idk)
        float sdfTng = length(p - vec2(0.0, -0.5)) - 0.1/s;
        float regTng = min(reg, 1.-smoothstep(0., 0.01, sdfTng));
        vec3 tngCol = vec3(1.,0.,0.) * (1.-smoothstep(-0.02, 0.01, sdfTng)) * outline;
        col = mix(col, tngCol, regTng);

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
