const float screenCurvature = 0.001;

vec2 distortCoordinates(vec2 coords, float w){
	vec2 cc = (coords - vec2(0.5, 0.5));
	float dist = dot(cc, cc) * w;
	return (coords + cc * (1. + dist) * dist);
}

vec3 gridPattern(vec2 coords) {
    float lineWidth = 0.02;
    vec2 grid = fract(coords * 10.0);
    float line = step(grid.x, lineWidth) + step(grid.y, lineWidth);
    line += step(1.0 - grid.x, lineWidth) + step(1.0 - grid.y, lineWidth);
    return vec3(line);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = distortCoordinates(uv, 1.0 * sin(iTime));
    // Time varying pixel color
    vec3 col = gridPattern(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}
