#define EPSILON 0.0001
#define MAX_DST 70.0
#define ITERATI 80



float glw(float d, float r, float i)
{


    return pow(r / max(d, 0.0001), i);	

}



mat2 rt(float t)
{

    float c = cos(t);
    float s = sin(t);
    return mat2(c, -s, s, c);

}


float cube(vec3 p, vec3 s)
{
    vec3 rp = p;
    rp.xy *= rt(iTime * 0.5);
    rp.zy *= rt(iTime * 0.75);
    rp = abs(rp) - s;
    return length(max(rp, 0.0)) + min(max(rp.x, max(rp.y, rp.z)), 0.0);
}



vec2 map(vec3 sp)
{


    float c_id = 0.01;

    float d0 = cube(sp, vec3(0.5));

    return vec2(d0, c_id);


}


vec2 mrch(vec3 ro, vec3 rd, inout float g)
{

    float d0 = 0.0;
    vec2 dsi = vec2(0.0);

    for (int i = 0; i < ITERATI; i++)
    {

        vec3 p = ro + rd * d0;
        p.z += iTime * 16.0;
        float z = (sin(iTime * 0.0001) * 0.5 + 0.5) * 8.0;
        p = mod(p, z) - z * 0.5;
        p.xz += sin(p.xz * 2.0 + iTime);

        dsi = map(p);
        float ds = dsi.x;
        g += glw(ds, 0.08, 1.5);
        d0 += ds;

        if (d0 > MAX_DST || abs(ds) < EPSILON) break;



    }

    return vec2(d0, dsi.y);

}



vec3 dir(vec2 uv, vec3 ro, vec3 fx)
{


    vec3 w = normalize(fx - ro);
    vec3 u = normalize(cross(w, vec3(0.0, 1.0, 0.0)));
    vec3 v = normalize(cross(u, w));

    return mat3(u, v, w) * normalize(vec3(uv, 1.5));

}


void mainImage(out vec4 c_out, in vec2 u)
{

    vec2 rr = iResolution.xy, uv = (u + u - rr) / rr.y;
    uv *= 0.4;
    uv *= rt(iTime * 0.25);
    uv = abs(uv);

    vec3 ro = vec3(0.0, 0.1,  -2.0);
    vec3 fx = vec3(0.0, -0.1, 0.1);
    uv.x += sin((iTime + uv.x + sin(uv.y * 2.0)) * 2.1) * 0.1;
    uv.xy *= rt(iTime * 0.1);
    vec3 rd = dir(uv, ro, fx);
    float g = 0.0;
    float fog = 0.25;
    float f = length(uv);
    vec2 sci = mrch(ro, rd, g);
    float d0 = sci.x;

    d0 = mix(fog, g, f);

    c_out = vec4(vec3(d0) * vec3(0.3, 1.0, 0.5), 1.0);

}
