void mainImage( out vec4 fragColor, in vec2 uv1 )
{
    vec3 col = vec3(0.);
    float uv_x = 50.*(uv1.x-0.5*iResolution.x)/iResolution.y;
    float uv_y = 50.*(uv1.y-0.5*iResolution.y)/iResolution.y;
    vec2 uv = vec2(uv_x,uv_y);

    float pi=3.141592;
    float c=mod(float(iFrame)/400.,pi)-pi/2.;
    float z=1.6;

    float r=-0.25;
    float r2=r*2.;
    float r3=r*4.;
    float k=4.;

    float v1=0.;
    float val1=0.;
    float val2=0.;
    vec2 holder = vec2(0.);

    for(float d=0.; d<=2.*pi; d+=pi/k){
    val1 = 10.*cos(c)-5.*cos(3.*c+d)-(2.5+z)*cos(9.*c+d);
    val2=-10.*sin(c)+5.*sin(3.*c+d)+(2.5+z)*sin(9.*c+d);
    holder = vec2(val1,val2);
    v1=max(v1,pow(2.,r*length(uv-holder)));   

    val1 = -10.*cos(c)-5.*cos(3.*c+d)-(2.5+z)*cos(9.*c+d);
    val2=10.*sin(c)+5.*sin(3.*c+d)+(2.5+z)*sin(9.*c+d);
    holder = vec2(val1,val2);
    v1=max(v1,pow(2.,r*length(uv-holder)));  

    val1 = 10.*cos(c+pi/2.)-5.*cos(3.*c+d)-(2.5+z)*cos(9.*c+d);
    val2=-10.*sin(c+pi/2.)+5.*sin(3.*c+d)+(2.5+z)*sin(9.*c+d);
    holder = vec2(val1,val2);
    v1=max(v1,pow(2.,r*length(uv-holder)));   

    val1 = -10.*cos(c+pi/2.)-5.*cos(3.*c+d)-(2.5+z)*cos(9.*c+d);
    val2=10.*sin(c+pi/2.)+5.*sin(3.*c+d)+(2.5+z)*sin(9.*c+d);
    holder = vec2(val1,val2);
    v1=max(v1,pow(2.,r*length(uv-holder)));  
    }

    float v2=0.;
    val1 = -10.*cos(c);
    val2=10.*sin(c);
    holder = vec2(val1,val2);
    v2=max(v2,pow(2.,r2*length(uv-holder)));

    val1 = 10.*cos(c);
    val2=-10.*sin(c);
    holder = vec2(val1,val2);
    v2=max(v2,pow(2.,r2*length(uv-holder)));  

    c=c+pi/2.;

    float v3=0.;
    for(float d=0.; d<=2.*pi; d+=pi/k){
    val1 = 10.*cos(c)-5.*cos(3.*c+d)-(2.5+z)*cos(9.*c+d);
    val2=-10.*sin(c)+5.*sin(3.*c+d)+(2.5+z)*sin(9.*c+d);
    holder = vec2(val1,val2);
    v3=max(v3,pow(2.,r*length(uv-holder)));   

    val1 = -10.*cos(c)-5.*cos(3.*c+d)-(2.5+z)*cos(9.*c+d);
    val2=10.*sin(c)+5.*sin(3.*c+d)+(2.5+z)*sin(9.*c+d);
    holder = vec2(val1,val2);
    v3=max(v3,pow(2.,r*length(uv-holder)));  

    val1 = 10.*cos(c+pi/2.)-5.*cos(3.*c+d)-(2.5+z)*cos(9.*c+d);
    val2=-10.*sin(c+pi/2.)+5.*sin(3.*c+d)+(2.5+z)*sin(9.*c+d);
    holder = vec2(val1,val2);
    v3=max(v3,pow(2.,r*length(uv-holder)));   

    val1 = -10.*cos(c+pi/2.)-5.*cos(3.*c+d)-(2.5+z)*cos(9.*c+d);
    val2=10.*sin(c+pi/2.)+5.*sin(3.*c+d)+(2.5+z)*sin(9.*c+d);
    holder = vec2(val1,val2);
    v3=max(v3,pow(2.,r*length(uv-holder)));  
    }

    val1 = -10.*cos(c);
    val2=10.*sin(c);
    holder = vec2(val1,val2);
    v2=max(v2,pow(2.,r2*length(uv-holder)));

    val1 = 10.*cos(c);
    val2=-10.*sin(c);
    holder = vec2(val1,val2);
    v2=max(v2,pow(2.,r2*length(uv-holder)));  

    val1 = 0.;
    val2=0.;
    holder = vec2(val1,val2);
    v2=max(v2,pow(2.,r3*length(uv-holder)));      


    col+=vec3(v1,v2,v3);



    fragColor = vec4(col,1.0);

}
