void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 5.*( u+u - R ) / R.y;
    O *= 0.;           // 9/4
    for (float i,n,d, x = 2.25; i++<4.; U = vec2(-U.y,U) )      // 4 quadrants
        n = 9.,                                                 // number of isovalues
        d = 4.* length( U - vec2( x, 
                                    U.x<x && U.y>x ? n=4., x    // "corner"
                                  : U.x>x ? 1. : 0.             // 2 "spirals" 
                      )         ),
        d < n
          ? O += sqrt(max(0., 1. - R.y/40. / abs(tan(3.14*d)*3.14)) ) // draw isovalues
          : O;  // ^ to sRGB                
}






/** // 257 chars


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 2.5*( u+u - R ) / R.y;
    O *= 0.;             // 9/8
    for (float i,n,d ,x = 1.125; i++<4.; U = vec2(-U.y,U) )       // 4 quadrants
        n = 9.,                                                   // number of isovalues
        d = 8.* length( U - (
                              U.x<x && U.y>x ? n=4., vec2(x)      // "corner"
                            : vec2( x, U.x>x ? .5 : 0. )          // 2 "spirals" 
                      )     ),
        d < n
          ? // d = cos(3.1416*d), O += max(0., 1. - abs(d)/fwidth(d))
            O += max(0., 1. - R.y/5. / abs(tan(3.14*d)*8.*3.14) ) // draw isovalues
          : O;

    O = sqrt(O);                                                  // to sRGB
}

/**/
