void mainImage( out vec4 Col, in vec2 Coord )
{
    float x = Coord.x/iResolution.x;
    vec3 v = vec3(iDate.z/31.f, iDate.y/12.f, iDate.x/3000.f);
    vec3 c = v;
    float d = .25f;
    float f = 1.f/d;

    if (x < d * f)
    {
        f--;
    }

    if (x < d * f)
    {
        c = v.yzx;
        f--;
    }

    if (x < d * f)
    {
        c = v.zxy;
    }

    if (x < d)
    {
        c = v.zyx;
    }

    Col = vec4(c,1);
}
