float sdfSphere(vec3 p)
{
    return length(p)-0.9;
}
vec3 nsphere(vec3 p,vec3 sp)
{
      return normalize(p-sp);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    vec2 h = iResolution.xy*.5;
    float f = 2.;
    // Time varying pixel color
    vec3 tra = normalize(vec3(uv-vec2(iResolution.x/(iResolution.y*2.),.5),f));
    vec3 p = vec3(.0,.0,-f-10.);
    vec3 col = vec3(.0);
    for (float d=.0;d<1000.;d+=sdfSphere(p))
    {
        p+=tra*sdfSphere(p);
        if (sdfSphere(p)<.01)
        {
            col = vec3(dot(nsphere(p,vec3(.0)),vec3(normalize(iMouse.xy-h),.0)));
            break;
        }
    }
    // Output to screen
    fragColor = vec4(col,1.);
}
