void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    float t = 1.5*iTime;

    // Enhanced wave formula combining shadow effect
    float formula_waves_sim = 1.0 - 0.6*sin(5.0*(uv.x + uv.y + sin(5.1*uv.y) + 0.0012*t));
    float shadow = 0.7 * formula_waves_sim;

    // Amplified wave movement using shadow intensity
    uv.x += 0.06*sin(2.0*uv.y+t) * (1.0 + shadow);

    // Create dynamic shading with combined effects
    vec4 col = vec4(0.9,0.9,0.9,1) * vec4(shadow * (1.0 + 0.3*sin(uv.y*4.0 + t)));

    // Apply colors with enhanced wave-shadow interaction
    fragColor = vec4(1,0,0,0)*col;

    if(uv.x<(1.0/1.5))
        fragColor = vec4(1,1,0,0)*col;

    if(uv.x<1.0/3.0)
        fragColor = vec4(0,0,1,0)*col;
}
