const float PHI = 1.61803398874989484820459; // the golden ratio

float noise(in vec2 xy, in float seed) {
    return fract(tan(distance(xy * PHI, xy) * seed));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 r = 2.0 * vec2(fragCoord.xy - 0.5 * iResolution.xy);

    // float seed = iTime;
    float seed = fract(iTime);

    vec3 color = vec3(noise(r, seed));

