// Shader for "Invention of Agriculture" with earth-toned and natural colors representing growth, soil, and harvest.

// User parameters
uniform float tech_input;      // Technology input for sigmoid function

#define AA 2  // Set AA to 1 if your machine is too slow
#define METHOD 3

// Sigmoid function for technology growth
float sigmoid(float x) {
    return 1.0 / (1.0 + exp(-x));
}

// Function to compute the quaternion constant c based on the "Invention of Agriculture"
vec4 computeC(float time) {
    float health;
    float freedom;
    float interconnectivity;
    float tech_factor = sigmoid(tech_input);

    // CARE values for the "Invention of Agriculture"
    if (time < 0.33) {  // Early phase
        health = 5.0;
        freedom = 3.0;
        interconnectivity = 4.0;
    } else if (time < 0.66) {  // Acceleration phase
        health = 6.0;
        freedom = 5.0;
        interconnectivity = 6.0;
    } else {  // Maturity phase
        health = 7.0;
        freedom = 6.0;
        interconnectivity = 7.0;
    }

    // Normalize the CARE values and compute the quaternion constant
    return vec4(
        (5.0 + min(health, 8.5)) / 10.0 * (5.0 + min(freedom, 8.5)) / 10.0 * (5.0 + min(interconnectivity, 8.5)) / 10.0 * tech_factor,
        0.8 * (5.0 + health) / 10.0 * sin(time * 0.7),
        0.8 * (5.0 + freedom) / 10.0 * cos(time * 0.5),
        0.8 * (5.0 + interconnectivity) / 10.0
    );
}

vec4 qsqr(in vec4 a) {
    return vec4(
        a.x * a.x - a.y * a.y - a.z * a.z - a.w * a.w,
        2.0 * a.x * a.y,
        2.0 * a.x * a.z,
        2.0 * a.x * a.w
    );
}

const int numIterations = 500; // Higher number of iterations for better detail

// Function to map the fractal and limit bailout-radius to avoid disappearing
float map(in vec3 p, out vec4 oTrap, in vec4 c, out float iteration) {
    vec4 z = vec4(p, 0.0);
    float md2 = 1.0;
    float mz2 = dot(z, z);
    oTrap = vec4(abs(z.xyz), dot(z, z));

    iteration = 0.0;
    for (int i = 0; i < numIterations; i++) {
        md2 *= 4.0 * mz2;
        z = qsqr(z) + c;

        // Add some noise for organic deformation
        z.xyz += 0.3 * sin(6.0 * z.xyz + iTime * 0.05);

        // Orbit trap to enhance visual detail
        oTrap = min(oTrap, vec4(abs(z.xyz), dot(z, z)));
        mz2 = dot(z, z);
        iteration += 1.0;

        // Increase the bailout threshold significantly to ensure the fractal never disappears
        if (mz2 > 500.0) break;  // Significantly increased bailout threshold
    }

    // Return a smaller scaling factor to prevent it from vanishing
    return 0.05 * sqrt(mz2 / md2) * log(mz2);  // Further reduced scaling factor from 0.25 to 0.05
}

// Function to get colors representing agriculture: green, brown, and gold
vec3 getAgricultureColor(float health, float freedom, float interconnectivity) {
    float totalCARE = (health + freedom + interconnectivity) / 3.0;

    // Define natural, earthy colors
    vec3 green = vec3(0.0, 0.5, 0.2);  // Leafy green (RAL 6002)
    vec3 brown = vec3(0.47, 0.27, 0.23);  // Soil brown (RAL 8003)
    vec3 beige = vec3(0.9, 0.8, 0.6);  // Earthy beige (RAL 1001)
    vec3 golden = vec3(1.0, 0.85, 0.3);  // Golden for harvest (RAL 1037)

    // Gradual transitions between agriculture-inspired colors
    if (totalCARE <= 5.0) {
        return mix(green, brown, totalCARE / 5.0);  // Transition from green to brown
    }
    if (totalCARE <= 6.0) {
        return mix(brown, beige, (totalCARE - 5.0));  // Transition from brown to beige
    }
    if (totalCARE <= 7.0) {
        return mix(beige, golden, (totalCARE - 6.0));  // Transition from beige to golden
    }
    return golden;  // Harvest gold for highest CARE values
}

// Function to adjust color based on the CARE-scores using agriculture colors
vec3 getColor(float iteration, float maxIteration, vec4 oTrap, float health, float freedom, float interconnectivity) {
    float t = iteration / maxIteration;

    // Get agriculture-based color depending on CARE values
    vec3 baseColor = getAgricultureColor(health, freedom, interconnectivity);

    // Add orbit trap influence to color for more visual complexity
    baseColor += 0.2 * vec3(oTrap.x, oTrap.y, oTrap.z) * (1.0 - t);
    return baseColor;
}

float intersect(in vec3 ro, in vec3 rd, in vec4 c, out vec4 res, out float iteration) {
    vec4 tmp;
    float resT = -1.0;
    float maxd = 25.0;  // Increased maximum distance
    float h = 1.0;
    float t = 0.0;
    for (int i = 0; i < 500; i++) {
        if (h < 0.0001 || t > maxd) break;
        h = map(ro + rd * t, tmp, c, iteration);
        t += h;
    }
    if (t < maxd) {
        resT = t;
        res = tmp;
    }
    return resT;
}

// Function to render the fractal and ensure it remains visible
vec3 render(in vec3 ro, in vec3 rd, in vec4 c, float health, float freedom, float interconnectivity) {
    const vec3 sun = vec3(0.577, 0.577, 0.577);

    vec4 tra;
    float iteration;
    float t = intersect(ro, rd, c, tra, iteration);

    vec3 col;
    if (t < 0.0) {
        // Background gradient with a crÃ¨me color
        col = vec3(0.94, 0.87, 0.78) * (0.7 + 0.3 * rd.y); // Beige background
    } else {
        vec3 mate = vec3(0.8, 0.0, 0.0) * 0.3;
        vec3 pos = ro + t * rd;
        vec3 nor = normalize(pos);  // Normal approximation

        // Get color based on iteration count and orbit trap using agriculture colors
        col = getColor(iteration, float(numIterations), tra, health, freedom, interconnectivity);

        // Add some subsurface scattering effect for an organic feel
        float subsurface = clamp(dot(rd, nor) + 0.5, 0.0, 1.0);
        col += mate * 0.4 * subsurface;

        // Sun lighting
        vec3 lig = sun;
        float dif = clamp(dot(lig, nor), 0.0, 1.0);
        col += mate * vec3(1.00, 0.90, 0.70) * dif;

        // Soft shadows for a more organic look
        float shadow = 0.8 + 0.2 * cos(t + iTime * 0.05);
        col *= shadow;

        // Add variation in brightness for more depth
        col *= 0.8 + 0.2 * sin(iTime * 0.05 + iteration);
    }

    return pow(col, vec3(0.4545));  // Gamma correction for realistic brightness
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Parameters for animation
    float time = iTime * 0.0025;  // Slower movement for a calmer effect
    vec4 c = computeC(time);

    // Set up the camera
    float r = 3.0;  // Adjust zoom level here
    vec3 ro = vec3(r * cos(0.3 + 0.37 * time), 0.3 + 0.8 * r * cos(1.0 + 0.33 * time), r * cos(2.2 + 0.31 * time));
    vec3 ta = vec3(0.0, 0.0, 0.0);
    float cr = 0.1 * cos(0.1 * time);

    // Compute camera direction vectors
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(sin(cr), cos(cr), 0.0);
    vec3 cu = normalize(cross(cw, cp));
    vec3 cv = normalize(cross(cu, cw));

    // Ray direction
    vec2 p = (-iResolution.xy + 2.0 * fragCoord) / iResolution.y;
    vec3 rd = normalize(p.x * cu + p.y * cv + 2.0 * cw);

    // CARE parameters for the "Invention of Agriculture"
    float health, freedom, interconnectivity;
    if (time < 0.33) {
        // Early phase
        health = 5.0;
        freedom = 3.0;
        interconnectivity = 4.0;
    } else if (time < 0.66) {
        // Acceleration phase
        health = 6.0;
        freedom = 5.0;
        interconnectivity = 6.0;
    } else {
        // Maturity phase
        health = 7.0;
        freedom = 6.0;
        interconnectivity = 7.0;
    }

    // Render the scene with agriculture-based color scheme
    vec3 col = render(ro, rd, c, health, freedom, interconnectivity);

    fragColor = vec4(col, 1.0);
}
