//#define DO_DISCARD

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float x = (uv.x-0.5);
    float y = (uv.y-0.5);
    vec4 tex = texture(iChannel0, uv, 0.);
    bool do_disort = mod(iTime,10.)<5.;

    if(do_disort) {
    	y *= sin(tex.r+x*60.);
        x *= 1.+sin(tex.g+y*(40.+sin(iTime*0.25)*20.));
    } else {
    	y *= sin(x*60.);
        //x *= 1.+sin(y*60.);
        x *= 1.+sin(y*(40.+sin(iTime*0.25)*20.));
    }

    y *= (iResolution.y/iResolution.x);
    float r;
    r = (sin(iTime*1.)+1.)/2.;
    r = sqrt(x*x+y*y)*2./r;
    float br = r*1.;
    float e = 0.5;
    float f = 5.;
    br = 1./( e+ ((br<e)?(e-br):(br-e)) *f);
    fragColor = vec4(0.4, 1.0*((sin(iTime*2.)+1.)/2.), 0.9, 1.)*br;
#if defined(DO_DISCARD)
    if(br<0.7)
        fragColor = vec4(0.,0.5,0.,1.);// discard;
#endif
}
