float roundBox(vec2 coord, vec2 pos, vec2 b ){
  return 1.-floor(length(max(abs(coord-pos)-b,0.0)));
}
mat2 rotate(float Angle)
{
    mat2 rotation = mat2(
        vec2( cos(Angle),  sin(Angle)),
        vec2(-sin(Angle),  cos(Angle))
    );
	return rotation;
}
float box(vec2 coord, vec2 pos, vec2 size){
	if((coord.x<(pos.x+size.x)) &&
	   (coord.x>(pos.x-size.x)) &&
	   (coord.y<(pos.y+size.y)) && 
	   (coord.y>(pos.y-size.y)) ) 
		return 1.0;
	return 0.0;
}
float sun(vec2 coord, vec2 pos, float size){
	if(length(coord-pos)<size)
		return 1.0;
	return 0.0;
}
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
float arrow(vec2 coord, vec2 pos){
	return box(coord, pos, vec2(.4,.1))+box(coord, pos+vec2(.3,-.3), vec2(.1,.4));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	float t=iTime;
	vec4 tulos;
	vec4 lopullinentulos=vec4(1.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
	float aspectCorrection = (iResolution.x/iResolution.y);
	vec2 coordinate_entered = 2.0 * uv - 1.0;
	vec2 c = vec2(aspectCorrection,1.0) *coordinate_entered;
	for(float rgbare=0.0; rgbare<2.0; rgbare++){
	vec2 coord = vec2(aspectCorrection,1.0) *coordinate_entered;
	coord.x*=1.0+rgbare*0.009;
	tulos=vec4(vec3(255.0/255.0, 70.0/255.0, 65.0/255.0),1.0);
	for(float i=0.; i<12.; i++){
		tulos+=4.*sun(coord, vec2(.5+sin(t*.1+i)*.4+rand(vec2(i,2.)*.1),mod(t*.2+i+t*i*.00123+rand(vec2(i,2.)),5.0)-2.5), .4*rand(vec2(i,4.3)))*15.;
		tulos-=sun(coord, vec2(.5+sin(t*.1+i)*.4+rand(vec2(i,2.)*.1),mod(t*.2+i+t*i*.00123+rand(vec2(i,2.)),5.0)-2.5), .4*rand(vec2(i,4.3))+0.04)*5.;
	}
	tulos+=arrow(vec2(mod(c.x-t*.5,1.2),mod(c.y-t*.3,1.)),vec2(.7))/3.;
    vec2 pos=vec2(-.6,-.2+.1*sin(t*4.));
	float k1=floor(mod(iTime,2.))*.1;
	float k2=floor(mod(iTime+1.,2.))*.1;
	coord*=rotate(floor(mod(iTime*.3,2.0))*.2-.1);
	tulos-=	(box(coord*vec2(1.), vec2(.2,-.34-k1)+pos, vec2(.1,.2))+
			box(coord*vec2(1.), vec2(-.2,-.34-k2)+pos, vec2(.1,.2))+
			box(coord*vec2(1.), vec2(0.,.2)+pos, vec2(.4,.3))+
			box(coord*vec2(1.), vec2(.55,.3-k2)+pos, vec2(.1,.2))+
			box(coord*vec2(1.), vec2(-.55,.3-k1)+pos, vec2(.1,.2))+
			box(coord*vec2(1.), vec2(0.,.75)+pos, vec2(.3,.2))-
			box(coord*vec2(1.), vec2(0.,.75)+pos, vec2(.2,.1))*4. ) * 4.;	
	if(rgbare==0.0)
		lopullinentulos.r=tulos.r;
	if(rgbare==1.0)
		lopullinentulos.gb=tulos.gb;
	}
	if(mod(fragCoord.y,2.0)<1.0)
	lopullinentulos.xyz=lopullinentulos.xyz/1.3;
	float vignette = min(max(1.4 / (1.25 + 0.28*dot(c, c)),0.),1.);
	fragColor = lopullinentulos*vignette-0.01+rand(c*t)*0.02;
}
