void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = texture(iChannel0, uv);

	fragColor = color;
}

// Also taken from shader book
float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

mat2 rotate2d(float _angle){
    return 0.5 + 0.5*mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float wave(float p, float freq, float amp){
    return 0.5 + 0.5*sin(p * freq + iTime)*amp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.st/iResolution.st;

    vec4 tex = texture(iChannel2, st);

    st -= vec2(0.5);
    st *= rotate2d(tex.r*.001)
        	*rotate2d(tex.y*.01)
        	*wave(1.0, 10.0, iTime*random(vec2(0.0, 1.0)));
    //st *= mat2(0.99, 0.0, 0.0, 0.99);
    st += vec2(0.5);

    vec2 texel = 1.0/iResolution.xy;

    vec2 offset[9];

    offset[0] = vec2(-texel.x, -texel.y);
    offset[1] = vec2(     0.0, -texel.y);
    offset[2] = vec2( texel.x, -texel.y);
    offset[3] = vec2(-texel.x,      0.0);
    offset[4] = vec2(     0.0,      0.0);
    offset[5] = vec2( texel.x,      0.0);
    offset[6] = vec2(-texel.x,  texel.y);
    offset[7] = vec2(     0.0,  texel.y);
    offset[8] = vec2( texel.x,  texel.y);

    float kernel[9];

    kernel[0] = -1.0; kernel[1] = -1.0; kernel[2] = -1.0;
    kernel[3] = -1.0; kernel[4] = 8.0; kernel[5] = -1.0;
    kernel[6] = -1.0; kernel[7] = -1.0; kernel[8] = -1.0;

    vec4 sum = vec4(0.0);

    for (int i = 0; i < 9; i++){
        sum += texture(iChannel0, st + offset[i]) * kernel[i];
    }


    vec4 tex0 = texture(iChannel0, st);
    vec4 tex1 = vec4(clamp(sum.rgb, 0.0, 1.0), 1.0)*(0.5 + 0.5*wave(20.0, 600.0, 1.0)*iTime);

    vec4 color = mix(tex0, tex1, 0.5*iTime*0.01);


    fragColor = color;
}
