float SdCircle(vec2 p, float r) {
	return length(p) - r;
}

float Union(float d1, float d2) {
	return min(d1, d2);
}

float Map(vec2 p) {
	float res = Union(SdCircle(p + vec2(-0.5, 0.0), 0.6), SdCircle(p + vec2(0.5, 0.0), 0.6));
    return res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float dist = Map(uv);
    vec3 o_color = vec3(1.0, 1.0, 1.0);
    if (dist > -0.05 && dist < 0.05) o_color = vec3(0.0, 0.0, 0.0);
    else if (dist < -0.005f) o_color = vec3(0.5, 0.5, 0.1);
	fragColor = vec4(o_color, 1.0);
}
