const float TWOPI = 6.28318530718;
const vec3 one = vec3(1.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float time = iTime * 0.33;
    vec2 uv = -fragCoord / iResolution.xy;

    vec2 p0 = uv - vec2(0.5 + 0.5 * sin(1.4 * TWOPI * uv.x + 2.8 * time), 0.5);

    vec3 wave = vec3(
        0.5 * (cos(sqrt(dot(p0, p0)) * 5.6) + 1.0),
        cos(4.62 * dot(uv, uv) + time),
        cos(distance(uv, vec2(1.6 * cos(time * 2.0), 1.0 * sin(time * 1.7))) * 1.3)
    );

    float color = dot(wave, one) / 16.6;

    fragColor = vec4(
        0.5 * (sin(TWOPI * color + time * 3.45) + 1.0),
        0.5 * (sin(TWOPI * color + time * 3.15) + 1.0),
        0.4 * (sin(TWOPI * color + time * 1.26) + 1.0),
        1.0
	);
}
