const float speed = 1.5;
const float widthFactor = 4.0;

vec3 calcSaw(vec2 uv, 
              float frequency, float amplitude, float shift, float offset,
              vec3 color, float width, float exponent)
{
    float angle = iTime * speed * frequency + (shift + uv.x) * 4.0;

    float y = fract(angle);

    y = mix(y, 1.0 - y, mod(angle, 2.0) - y) * 2.0 - 1.0;

    y = y * amplitude + offset;


    float scale = pow(smoothstep(width * widthFactor, 0.0, distance(y, uv.y)), exponent);

    return color * scale;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    color += calcSaw(uv, 2.0, 0.45, 0.0, 0.5, vec3(0.0, 0.0, 1.0), 0.1, 3.0);
    color += calcSaw(uv, 2.2, 0.45, 0.2, 0.5, vec3(0.0, 1.0, 0.0), 0.1, 3.0);
    color += calcSaw(uv, 2.4, 0.45, 0.4, 0.5, vec3(1.0, 0.0, 0.0), 0.1, 3.0);

    color += calcSaw(uv, 2.6, 0.4, 0.6, 0.5, vec3(0.5, 0.0, 1.0), 0.1, 4.0);
    color += calcSaw(uv, 2.8, 0.4, 0.8, 0.5, vec3(1.0, 0.5, 1.0), 0.1, 4.0);
    color += calcSaw(uv, 3.0, 0.4, 0.0, 0.5, vec3(1.0, 0.0, 0.5), 0.1, 4.0);

    color += calcSaw(uv, 2.25, 0.45, 0.5, 0.5, vec3(0.0, 0.5, 1.0), 0.1, 4.0);
    color += calcSaw(uv, 2.50, 0.45, 0.5, 0.5, vec3(0.5, 1.0, 0.5), 0.1, 4.0);
    color += calcSaw(uv, 2.75, 0.45, 0.5, 0.5, vec3(1.0, 0.5, 0.0), 0.1, 4.0);
    fragColor = vec4(color,1.0);
}
