void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Stretch and rotate over time
    float waveOne = sin(iTime/8.0 + uv.y) * 4.0;
    float waveTwo = sin(iTime/8.0 + uv.x) * 4.0;

    vec4 color = texture(iChannel0,uv - vec2(cos(iTime/60.0) * waveOne, sin(iTime/60.0) * waveTwo));

    // Distort iChannel0
    uv = uv + vec2(0.25 + sin(iTime/-25.0), cos(iTime/-25.0));
	uv += texture(iChannel0, uv, 6.0).rr;
	uv.x += sin(uv.y * 0.5 + iTime)/5.0; // Oscillation

    fragColor = texture(iChannel1, vec2(uv.x + sin(iTime/30.0), uv.y + cos(iTime/30.0))) * vec4(vec3(color.x),1.0);

    // Set global color to green.
    fragColor += vec4(float(int(color.r * 5.0))/10.0,
                      float(int(color.g * 8.0))/10.0,
                      float(int(color.b * 3.0))/10.0,
                      1);  
}
