float radioDistance(vec2 v, float r) {
    return r - sqrt(v.x * v.x + v.y * v.y);
}

void mainImage( out vec4 O, in vec2 u )
{
    vec4 color1 = vec4(1);
    vec4 color2 = vec4(0);

    // Rotate 45deg
	vec2 U = vec2(u.x - u.y, u.x + u.y);
    // Square modules
    vec2 module = mod(U, 60.) - 30.;
    float alpha = radioDistance(module, 20.);

    O = color1 * alpha + color2 * (1. - alpha);
}
