#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelP = fragCoord.xy / iResolution.xy;

    vec3 presentColor = vec3( 1.0, 0.0, 0.0 );

    float verticalRibbonX = 0.3;
    float horizontalRibbonY = 0.65;

    float ribbonWidth = 0.1;

    //	draw verticalRibbon
    presentColor *= smoothstep( ribbonWidth, ribbonWidth + 0.001, abs(pixelP.x - verticalRibbonX ));


    //	draw horizontalRibbon
    presentColor *= smoothstep( ribbonWidth, ribbonWidth + 0.001, abs( pixelP.y - horizontalRibbonY ));

    if( presentColor != vec3( 1.0, 0.0, 0.0 ) )
    {
    	presentColor = vec3( 1.0, 1.0, 1.0 );
    }

    vec2 bowPoint = vec2( 0.7, 0.65 );
    vec2 diffFromPixelToBowPoint = pixelP - bowPoint;
    float angle2BowPoint = atan( diffFromPixelToBowPoint.y, diffFromPixelToBowPoint.x );

    vec3 bowColor = vec3( 0.0, 1.0, 0.0 );

    float bowRadius = 0.2 + 0.1 * cos( angle2BowPoint * 4.0 + PI / 0.4 );
	if( bowRadius < 0.15 )
    {
    	bowRadius = 0.145;
    }

    presentColor *= smoothstep( bowRadius, bowRadius + 0.01, length( diffFromPixelToBowPoint) );

	fragColor = vec4( presentColor ,1.0);
}
