#define PI 3.1415926535
#define TWOPI 2.0 * PI

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 oneInc = vec2(1.0, 1.0) / iResolution.xy;

	vec2 uv = fragCoord.xy / iResolution.xy;

    float xSync = mod(uv.x + 0.1 * cos( iTime * 10.0 + uv.y * TWOPI * 0.1 ), 1.0);
    float ySync = mod(uv.y + 0.1 * cos( iTime * 10.0 + uv.x * TWOPI * 0.1 ), 1.0);

    vec2 sp = vec2( xSync, ySync );
    vec4 texture1 = texture( iChannel0, sp );    
    vec4 pc = texture1;    

	fragColor = vec4( pc );
}
