// Pass: Glow horizontal-blur and final output.

// Version 1.1:
// - Disabled cellular-automata based blur/glow.
// - Added a glow that uses a gaussian-like blur.
// - Fixed clicking in full-screen mode failing to spawn fractals (apparently mouse-coordinates are no longer texel-aligned in fullscreen).

// Version 1.2:
// - Added per-generation mutation to branch lengths, controlled by "kMaxGenerationMutation".

// Version 1.3:
// - To fix the shader's thumbnail failing to display fireworks for a while, forced always-on spawning.
// - To help avoid oddly clipped fireworks, added a gutter around the edge of the screen for placement of ambient fireworks.

vec2 sTexelSize;

vec3 hsbToRgb(
    vec3 hsbColor)
{
    // From: https://www.shadertoy.com/view/MsS3Wc
    vec3 rgb = clamp(abs(mod((hsbColor.x * 6.0) + vec3(0.0,4.0,2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    rgb = (rgb * rgb * (3.0 - (2.0 * rgb)));
    return (hsbColor.z * mix(vec3(1.0), rgb, hsbColor.y));
}

vec3 cellStateToCellColor(
    vec4 cellState)
{
    return hsbToRgb(vec3(
        fract(0.0471 * cellState.z), 
        0.9, 
        cellState.w));
}

float blurWeight(
    float radiusFraction)
{
    float result = smoothstep(1.0, 0.0, radiusFraction);
    result = (result * result * result);    
    return result;
}

void mainImage(
    out vec4 outFragColor,
    in vec2 fragCoord)
{
    sTexelSize = (1.0 / iResolution.xy);

    vec3 cellColor = cellStateToCellColor(texture(iChannel0, (fragCoord.xy * sTexelSize)));

    vec3 glowColor;
    {
    	glowColor = texture(iChannel1, (fragCoord.xy * sTexelSize)).rgb;

        vec3 accumulator = vec3(0.0);
        float totalWeight = 0.0;

        float oneOverBlurRadius = (1.0 / 10.0);

        for (float xDelta = -10.0; xDelta <= 10.0; xDelta++)
        {
            vec2 sampleCoord = (fragCoord.xy + vec2(xDelta, 0.0));
            vec3 sampleColor = texture(iChannel1, (sampleCoord * sTexelSize)).rgb;

            float sampleWeight = blurWeight(abs(xDelta) * oneOverBlurRadius);

            accumulator += (sampleColor * sampleWeight);
            totalWeight += sampleWeight;
        }

        glowColor = (accumulator / totalWeight);
    }

	outFragColor = vec4(
        mix(glowColor, cellColor, sign(dot(cellColor, cellColor))),
        1.0);

    outFragColor.rgb = pow(outFragColor.rgb, vec3(1.0 / 2.2));
}

// Pass: Simulation

// Cell format:
// x = remainingSteps
// y = direction
// z = edgeTotalSteps
// w = brightness

#define AMBIENT_FIREWORKS_ENABLED
//#define CELLULAR_BLUR_ENABLED

const float kAmbientFireworkPeriod = 3.0;
const float kFadeRate = 0.5;
const float kBlurFadeSteps = 50.0;
const float kMaxGenerationMutation = 1.0;

vec2 sTexelSize;

float randomFloat(
  vec2 testCoord)
{
	// From: https://www.shadertoy.com/view/Xd23Dh
	// (just removed some dimensions)
	float testCoordInGeneratorSpace = 
		dot(testCoord, vec2(127.1, 311.7));

	return fract(sin(testCoordInGeneratorSpace) * 43758.5453);
}

vec2 randomVec2(
  vec2 testCoord)
{
	// From: https://www.shadertoy.com/view/Xd23Dh
	// (just removed some dimensions)
	vec2 testCoordInGeneratorSpace = vec2(
		dot(testCoord, vec2(127.1, 311.7)),
		dot(testCoord, vec2(269.5, 183.3)));

	return fract(sin(testCoordInGeneratorSpace) * 43758.5453);
}

vec3 randomVec3(
  vec2 testCoord)
{
    // From: https://www.shadertoy.com/view/Xd23Dh
    // (just removed some dimensions)
    vec3 testCoordInGeneratorSpace = vec3(
        dot(testCoord, vec2(127.1, 311.7)),
        dot(testCoord, vec2(269.5, 183.3)),
        dot(testCoord, vec2(419.2, 371.9)));

	return fract(sin(testCoordInGeneratorSpace) * 43758.5453);
}

float wrapDirection(
    float unboundedDirectionIndex)
{
    return mod(unboundedDirectionIndex, 8.0);
}

void considerBlurCandidate(
    vec2 selfCoord,    
    vec2 candidateIndexDelta,
    inout vec4 inoutSelfState)
{
	vec2 candidateCoord = (selfCoord + candidateIndexDelta);
	vec4 candidateState = texture(iChannel0, (candidateCoord * sTexelSize));

    if (candidateState.w > inoutSelfState.w)
    {
        inoutSelfState = vec4(
            0.0,
            candidateState.y,
            candidateState.z,
            (candidateState.w - (kBlurFadeSteps * (kFadeRate * iTimeDelta))));
    }
}

void considerTrailCandidate(
    vec2 selfCoord,
    float selfToCandidateDirection,
    vec2 candidateIndexDelta,
    inout vec4 inoutSelfState,
	inout int inoutAcceptedCandidateCount)
{
	vec2 candidateCoord = (selfCoord + candidateIndexDelta);
	vec4 candidateState = texture(iChannel0, (candidateCoord * sTexelSize));

    if (candidateState.x > 0.0)
    {
        float candidateToSelfDirection = wrapDirection(selfToCandidateDirection + 4.0);

        // If this candidate is travelling, otherwise it's dying/splitting.
        if (candidateState.x > 1.1)
        {
            if (abs(candidateToSelfDirection - candidateState.y) < 0.5)
            {
                inoutSelfState = vec4(
                    (candidateState.x - 1.0),
                    candidateState.y,
                    candidateState.z,
                    1.0);

        		inoutAcceptedCandidateCount++;
            }
        }
        else
        {
            float leftTurnDirection = wrapDirection(candidateState.y + 1.0);
            float rightTurnDirection = wrapDirection(candidateState.y - 1.0);
            float newEdgeSteps = (candidateState.z - mix(1.0, (1.0 + kMaxGenerationMutation), randomFloat(selfCoord)));

            if (abs(candidateToSelfDirection - leftTurnDirection) < 0.5)
            {
                inoutSelfState = vec4(
                    newEdgeSteps,
                    leftTurnDirection,
                    newEdgeSteps,
                    1.0);

                inoutAcceptedCandidateCount++;
            }
            else if (abs(candidateToSelfDirection - rightTurnDirection) < 0.5)
            {
                inoutSelfState = vec4(
                    newEdgeSteps,
                    rightTurnDirection,
                    newEdgeSteps,
                    1.0);

                inoutAcceptedCandidateCount++;
            }
        }
    }
}

void mainImage(
    out vec4 outFragColor,
    in vec2 fragCoord)
{
    sTexelSize = (1.0 / iResolution.xy);

	vec4 selfState = texture(iChannel0, (fragCoord.xy * sTexelSize));

    // If the cell is not yet part of a trail, attempt to become part of a trail.
    if (selfState.x == 0.0)
    {
        vec4 trailState = selfState;
        int acceptedCandidateCount = 0;

        considerTrailCandidate(fragCoord, 0.0, vec2(1.0, 0.0), trailState, acceptedCandidateCount);
        considerTrailCandidate(fragCoord, 1.0, vec2(1.0, 1.0), trailState, acceptedCandidateCount);
        considerTrailCandidate(fragCoord, 2.0, vec2(0.0, 1.0), trailState, acceptedCandidateCount);
        considerTrailCandidate(fragCoord, 3.0, vec2(-1.0, 1.0), trailState, acceptedCandidateCount);
        considerTrailCandidate(fragCoord, 4.0, vec2(-1.0, 0.0), trailState, acceptedCandidateCount);
        considerTrailCandidate(fragCoord, 5.0, vec2(-1.0, -1.0), trailState, acceptedCandidateCount);
        considerTrailCandidate(fragCoord, 6.0, vec2(0.0, -1.0), trailState, acceptedCandidateCount);
        considerTrailCandidate(fragCoord, 7.0, vec2(1.0, -1.0), trailState, acceptedCandidateCount);

        // To avoid giving directional-preference, only accept trails when there are no conflicts.
        if (acceptedCandidateCount == 1)
        {
            selfState = trailState;
        }
    }
    else
    {
        // We were part of a trail, but we'll just turn into a fade-particle.
        selfState.x = 0.0;
    }

    // If the cell is alive, decay, otherwise blur.
    if (selfState.w > 0.0)
    {
        selfState.w -= (kFadeRate * iTimeDelta);

        // If the cell just died.
        if (selfState.w <= 0.0)
        {
            selfState = vec4(0.0);
        }
    }
    else
    {
		#ifdef CELLULAR_BLUR_ENABLED
        considerBlurCandidate(fragCoord, vec2(1.0, 0.0), selfState);
        considerBlurCandidate(fragCoord, vec2(1.0, 1.0), selfState);
        considerBlurCandidate(fragCoord, vec2(0.0, 1.0), selfState);
        considerBlurCandidate(fragCoord, vec2(-1.0, 1.0), selfState);
        considerBlurCandidate(fragCoord, vec2(-1.0, 0.0), selfState);
        considerBlurCandidate(fragCoord, vec2(-1.0, -1.0), selfState);
        considerBlurCandidate(fragCoord, vec2(0.0, -1.0), selfState);
        considerBlurCandidate(fragCoord, vec2(1.0, -1.0), selfState);
		#endif // CELLULAR_BLUR_ENABLED      
    }

    // If any mouse button is pressed.
    if (iMouse.z > 0.0)
    {
        vec2 fragToMouse = ((iMouse.xy + 0.5) - fragCoord.xy);

        if (dot(fragToMouse, fragToMouse) < 0.5)
        {
            //float edgeLength = mix(15.0, 25.0, randomFloat(fragCoord + vec2(iTime)));
            float edgeLength = 22.0;

			selfState = vec4(
                edgeLength, // remainingSteps
                2.0, // direction
                edgeLength, // edgeTotalSteps
                1.0); // brightness
        }
    }

    // Spawn ambient fireworks.
    #ifdef AMBIENT_FIREWORKS_ENABLED
    {
        float currentFireworkIndex = floor(iTime / kAmbientFireworkPeriod);
        float currentFireworkPeriodStartTime = (kAmbientFireworkPeriod * currentFireworkIndex);

        float ambientFireworkTime = (currentFireworkPeriodStartTime + randomFloat(vec2(currentFireworkIndex, 0.0)));

        bool shaderIsThumbnail = (iResolution.y < 300.0);

        // If the firework was located in the previous frame.
        bool fireworkTriggered = (((iTime - iTimeDelta) <= ambientFireworkTime) && (ambientFireworkTime < iTime));

        // To force the thumbnail-view to work, launch fireworks unconditionally.
        fireworkTriggered = (shaderIsThumbnail ? true : fireworkTriggered);

        if (fireworkTriggered)
        {
            float placementGutterFraction = 0.2;

            vec2 randomPlacementFraction = mix(
                vec2(placementGutterFraction), 
                vec2(1.0 - placementGutterFraction), 
                randomVec2(vec2(currentFireworkIndex, 1.1)));

            randomPlacementFraction = (shaderIsThumbnail ? vec2(0.5, 0.25) : randomPlacementFraction);

            vec2 fireworkCoord = (floor(randomPlacementFraction * iResolution.xy) + 0.5);

            vec2 fragToFirework = (fireworkCoord - fragCoord.xy);

            if (dot(fragToFirework, fragToFirework) < 0.5)
            {
                float edgeLength = mix(15.0, 25.0, randomFloat(vec2(currentFireworkIndex, 2.0)));

                selfState = vec4(
                    edgeLength, // remainingSteps
                    2.0, // direction
                    edgeLength, // edgeTotalSteps
                    1.0); // brightness
            }
        }
    }
	#endif // AMBIENT_FIREWORKS_ENABLED

    outFragColor = selfState;
}

// Pass: Glow vertical-blur.

vec2 sTexelSize;

vec3 hsbToRgb(
    vec3 hsbColor)
{
    // From: IÃ±igo Quiles 
    // https://www.shadertoy.com/view/MsS3Wc
    vec3 rgb = clamp(abs(mod((hsbColor.x * 6.0) + vec3(0.0,4.0,2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    rgb = (rgb * rgb * (3.0 - (2.0 * rgb)));
    return (hsbColor.z * mix(vec3(1.0), rgb, hsbColor.y));
}

vec3 cellStateToCellColor(
    vec4 cellState)
{
    return hsbToRgb(vec3(
        fract(0.0471 * cellState.z), 
        0.9, 
        cellState.w));
}

float blurWeight(
    float radiusFraction)
{
    float result = smoothstep(1.0, 0.0, radiusFraction);
    result = (result * result * result);
    return result;
}

void mainImage(
    out vec4 outFragColor,
    in vec2 fragCoord)
{
    sTexelSize = (1.0 / iResolution.xy);

    vec3 glowColor;
    {
        vec3 accumulator = vec3(0.0);
        float totalWeight = 0.0;

        float oneOverBlurRadius = (1.0 / 10.0);

        for (float yDelta = -10.0; yDelta <= 10.0; yDelta++)
        {
            vec2 sampleCoord = (fragCoord.xy + vec2(0.0, yDelta));            
            vec3 sampleColor = cellStateToCellColor(texture(iChannel0, (sampleCoord * sTexelSize)));

            float sampleWeight = blurWeight(abs(yDelta) * oneOverBlurRadius);

            accumulator += (sampleColor * sampleWeight);
            totalWeight += sampleWeight;
        }

        glowColor = (accumulator / totalWeight);
    }

    outFragColor = vec4(glowColor, 1.0);
}
