float isGreaterThan(float a, float b) {
	if (a > b)
    {
        return 1.0;
    } else {
    	return 0.0;   
    }
}

vec3 isGreaterThan(vec3 a, vec3 b) {
 	return vec3(isGreaterThan(a.r, b.r), isGreaterThan(a.g, b.g), isGreaterThan(a.b, b.b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 noise = texture(iChannel1, uv * (iResolution.xy / vec2(256.0))).rgb;

    vec3 vid = texture(iChannel0, uv).rgb;

    fragColor = vec4(isGreaterThan(vid, noise), 1.0);
}
