void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;
	vec3 ret;
    if(mod(p.x,0.2) > 0.1) {       
    	ret = texture(iChannel1, p).xyz;
    } else {
        p.x += 0.2;
     	ret = texture(iChannel1, p).xyz;
    }
        vec3 pixel = ret;
    fragColor = vec4(pixel,1.0);
}
