void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 circle_center = iResolution.xy/2.0; //pixels
    float circle_radius = 10.0;

    //while(circle_radius> 200.0)
        //circle_radius -=200.0;
    //circle_radius = mod(circle_radius, 200.0);

    float motion_radius = 50.0;
	circle_center += vec2(sin(iTime), cos(iTime)) * motion_radius;

    float pixel_to_center_distance = sqrt(
       pow(circle_center.x- fragCoord.x, 2.0) +
        pow(circle_center.y - fragCoord.y, 2.0));

    if( abs(pixel_to_center_distance - circle_radius) < 5.0 ) // abs(...)
        fragColor = vec4(1,0,0,1.0);
    else
        fragColor = vec4(0,0,0,1.0);


}
