vec3 color = vec3(1.0, 0.2, 0.1);
vec3 lDir = vec3(0.0, 1.0, 0.0);
vec3 lColor = vec3(1.0,1.0,1.0);


float function(vec2 p)
{
    return sin(p.x) * cos(p.y);
}

float testFunction(vec3 p)
{
    return p.y - function(p.xz);
}


vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 100.0;
    float delta = 0.1;

	float precis = 0.002;
    float resT= tmin;
    float m = -1.0;
    for( float t=tmin; t<tmax; t+= delta )
    {
	    float res = testFunction(ro+rd*t);
        if( res <precis) {
            resT = t - 0.5 * delta;
        	m = 1.0;
            break;
        }
    }

    return vec2( resT, m );
}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    testFunction(pos+eps.xyy) - testFunction(pos-eps.xyy),
	    testFunction(pos+eps.yxy) - testFunction(pos-eps.yxy),
	    testFunction(pos+eps.yyx) - testFunction(pos-eps.yyx) );
	return normalize(nor);
}


vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0);
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;

    //hit
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );

        // lighting
		float dif = clamp( dot( nor, lDir ), 0.0, 1.0 );
        col = color*dif*lColor;
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, in vec3 up )
{
	vec3 w = normalize(ta-ro);
	vec3 u = normalize( cross(w,up) );
	vec3 v = normalize( cross(u,w) );
    return mat3( u, v, w );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

	// camera	
	vec3 ro = vec3( 0.0, 20.0, 16.0);
    vec3 ta = vec3( 10.0*mo.x, 10.0*mo.y, 0.0 );
    vec3 up = vec3 (0.0, 1.0, 0.0);

	// camera-to-world transformation
    mat3 ca = setCamera( ro, ta, up);

    // ray direction
	vec3 rd = ca * normalize( vec3(p.xy, 2.0) );

    // render	
    vec3 col = render( ro, rd );

    fragColor=vec4( col, 1.0 );
}
