float rand(vec2 co){
    return fract(sin(dot(co * 0.1, vec2(12.9898, 78.233))) * 43758.5453);
}

float mymix(float v1, float v2, float amt){
    float edge = 0.25;
    float aamt = (amt - edge) * (1.0 / (1.0 - 2.0 * edge));
    if (amt < edge)
        aamt = 0.0;
    else if (amt > (1.0 - edge))
        aamt = 1.0;
    return mix(v1, v2, aamt);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = vec2(48, 27);	// 1920x1080 / 40
    vec2 uv = fragCoord / iResolution.xy * res;
    float fade = max((res.y * 0.7 - uv.y) / res.y * 2.0, 0.0);
    float botAmt = max((res.y * 0.4 - uv.y) / res.y * 2.0, 0.0);

    uv.y -= iTime * res.y * 0.5;
    vec2 fl = floor(uv);
    vec2 amt = uv - fl;

    float v1 = rand(fl);
    float v2 = rand(vec2(fl.x + 1.0, fl.y));
    float a1 = mymix(v1, v2, amt.x);

    v1 = rand(vec2(fl.x, fl.y + 1.0));
    v2 = rand(fl + 1.0);
    float a2 = mymix(v1, v2, amt.x);

    float v = mix(mymix(a1, a2, amt.y), 1.0, botAmt);

    vec3 fv = vec3(v * 2.0, (v - 0.3) * 1.5, (v - 0.65) * 3.0) * fade;

	fragColor = vec4(fv, 1.0);
}
