void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec3 sceneColor = texture( iChannel0, uv ).rgb;
    vec3 noiseColor = texture( iChannel1, uv * 4.0 + vec2(iTime * 2.0, 0) ).rgb;
    vec3 noiseColor1 = texture( iChannel1, uv * 2.0 + vec2(iTime * 2.0, 0) ).rgb;
    vec3 noiseColor2 = texture( iChannel1, uv * 1.0 + vec2(iTime * 2.0, 0) ).rgb;
    fragColor.a = 1.0;
    float t = max( max(dot( noiseColor, vec3(0.34)), dot( noiseColor1, vec3(0.34))), dot( noiseColor2, vec3(0.34)));
    t *= 1.5; //blowout 
    fragColor.rgb = mix(sceneColor, vec3(0.7, 0.4, 0.0), t * (0.5 + 0.5*sin(iTime)));
}
