void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 x = vec4(uv,0.5+0.5*sin(iTime),1.0);
    uv = uv*5.-2.5;
    uv.x*=iResolution.x/iResolution.y;
    uv.y+=cos(.3*iTime);
    uv.x+=sin(.3*iTime);

    float c = sin(uv.x*uv.x+uv.y*uv.y-2.*iTime);
    fragColor = mix(vec4(c),x,.94);
}
