const float PI = 3.14159265358979;

const float period = 5.0;
const float a = 0.4;
const float offset = 2.5;

float line(vec2 p, float width){
    return (1.0 - step(width*0.5, abs(p.y))) * (1.0 - step(0.0, p.x) - step(p.x, -1.0));
}

mat2 rot(float t){
    return mat2(cos(t), -sin(t), sin(t), cos(t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy*2.0-iResolution.xy) / min(iResolution.x, iResolution.y);

    float t = mod(iTime, period);

    vec3 draw = vec3(0.0);

    vec3 color = vec3(0.01, 1.0, 0.01);

    vec2 pos = p;

    // cross
    pos = vec2(pos.x*(sin(pos.y+t*PI*a+offset))/2.0, pos.y*(sin(pos.x+t*PI*a+offset))/2.0);

    // change line width
    pos = vec2(pos.x*(sin(-t*PI*a+offset)+1.001)/2.0, pos.y*(cos(-t*PI*a+offset)+1.001)/2.0);

    // spiral
    pos *= rot((sin(t*PI*a+offset)+1.0)/length(p));

    // rotation
    pos *= rot(t*PI*2.0);

    float width = 0.01;
    draw += line(pos, width) * color;

    // Output to screen
    fragColor = vec4(draw,1.0);
}
