// parameters
#define progress mod(iTime /4.0, 1.0)
#define minRadius 0.1
#define maxRadius 0.18
#define blur 0.003
#define glow 0.1

#define PI 3.14159265358979

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - vec2(0.5, 0.5);
    float r = uv.x * uv.x + uv.y * uv.y;
    float col = (1.0 - smoothstep(maxRadius, maxRadius + blur, r)) * smoothstep(minRadius, minRadius + blur, r);
    float gl = (1.0 - smoothstep(minRadius, maxRadius + glow, r)) * smoothstep(minRadius - glow, maxRadius, r);
    float a = (PI + atan(uv.y, uv.x)) / (PI * 2.0);
	float p = 1.0 - progress;
    col *= smoothstep(p, p + blur, a);
    gl *= smoothstep(p - glow * 0.5, p + glow * 0.5, a);
    fragColor = vec4(col + gl, col, col, col + gl);
}
